import React, { useState, useEffect } from "react";
import EmployeeTable from "./components/EmployeeTable";

export default function App() {
  const [employees, setEmployees] = useState([]);
  const [form, setForm] = useState({
    name: "",
    email: "",
    role: "",
    department: "",
    phone: ""
  });

  const fetchEmployees = async () => {
    const res = await fetch("http://localhost:5000/api/employees");
    const data = await res.json();
    setEmployees(data);
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const addEmployee = async () => {
    if (!form.name || !form.email) return;
    await fetch("http://localhost:5000/api/employees", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form)
    });
    setForm({ name: "", email: "", role: "", department: "", phone: "" });
    fetchEmployees();
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-3xl font-bold text-gray-800 mb-6 border-b-4 border-blue-500 inline-block pb-1">
          Employee Management System
        </h1>

        {/* Add Employee Form */}
        <div className="bg-white shadow-md rounded-lg p-6 mb-6">
          <h2 className="text-xl font-semibold mb-4 text-gray-700">
            Add New Employee
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
            <input
              name="name"
              value={form.name}
              onChange={handleChange}
              placeholder="Name"
              className="border rounded px-3 py-2 focus:ring-2 focus:ring-blue-400"
            />
            <input
              name="email"
              value={form.email}
              onChange={handleChange}
              placeholder="Email"
              className="border rounded px-3 py-2 focus:ring-2 focus:ring-blue-400"
            />
            <input
              name="role"
              value={form.role}
              onChange={handleChange}
              placeholder="Role"
              className="border rounded px-3 py-2 focus:ring-2 focus:ring-blue-400"
            />
            <input
              name="department"
              value={form.department}
              onChange={handleChange}
              placeholder="Department"
              className="border rounded px-3 py-2 focus:ring-2 focus:ring-blue-400"
            />
            <input
              name="phone"
              value={form.phone}
              onChange={handleChange}
              placeholder="Phone"
              className="border rounded px-3 py-2 focus:ring-2 focus:ring-blue-400"
            />
          </div>
          <button
            onClick={addEmployee}
            className="mt-4 bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition"
          >
            Add Employee
          </button>
        </div>

        {/* Employee Table */}
        <div className="bg-white shadow-lg rounded-lg p-4">
          <EmployeeTable employees={employees} refresh={fetchEmployees} />
        </div>
      </div>
    </div>
  );
}
